import React, { useState } from 'react'
import { Link, Redirect } from 'react-router-dom'
import './LoginRegister.css'
import { connect } from 'react-redux'
import PropTypes from 'prop-types'
import { login } from '../../../actions/auth'

// @resources https://www.bignerdranch.com/blog/react-data-layer-part-3-login/
const Login = ({ login, isAuthenticated }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  })

  const { email, password } = formData

  const onChange = e =>
    setFormData({ ...formData, [e.target.name]: e.target.value })

  const onSubmit = async e => {
    e.preventDefault()
    login(email, password)
  }
  // if login success, redirect
  if (isAuthenticated) {
    return <Redirect to='/dashboard' />
  }

  return (
    <div className='LoginRegisterContainer'>
      <h1 className='sign-in'>Login</h1>
      <p className='lead'>
        <i className='fas fa-user' /> Login to your account.</p>
      <form className='form' onSubmit={e => onSubmit(e)}>
        <div className='form-group'>
          <input
            type='email'
            placeholder='Email Address'
            name='email'
            value={email}
            onChange={e => onChange(e)}
            required
          />
        </div>
        <div className='form-group'>
          <input
            type='password'
            placeholder='Password'
            name='password'
            value={password}
            onChange={e => onChange(e)}
            required />
        </div>
        {/* Might need this later */}
        {/* <div id='validator' className='alert alert-danger' style={{ display: 'none' }}>Invalid credentials</div> */}
        <input type='submit' className='btn log' value='Login' />
      </form>
      <p className='haveAcctQ'>
        Don't have an account? <Link to='/register'>Create&nbsp;One!</Link>
      </p>
    </div>
  )
}

Login.propTypes = {
  login: PropTypes.func.isRequired,
  isAuthenticated: PropTypes.bool
}

// check if state is authenticated
const mapStateToProps = state => ({
  isAuthenticated: state.auth.isAuthenticated
})

export default connect(
  mapStateToProps,
  { login }
)(Login)
